import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from 'react';
import {io} from "socket.io-client";
import { Box, Card, Container, IconButton, InputAdornment, InputLabel, OutlinedInput, Typography } from '@mui/material';


const ChatWindow = () => {

    const [socket, setSocket] = useState(null);
    const [message, setMessage] = useState("");
    const [chat, setChat] = useState([]);
    const [typing, setTyping] = useState(false);
  useEffect(() => {
    setSocket(io('http://localhost:4000'));
  }, []);

  useEffect(() => {
    if(!socket){return}
    socket.on('message-from-server',(data) => {
       setChat((prev) => [...prev,{message:data.message, recieved:true}])
    })
    socket.on('typing-started-from-server',() => {
        setTyping(true);
     })
  }, [socket]);

  const handleInput = (e) => {
    setMessage(e.target.value);
    socket.emit('typing-started');
  }

  const handleForm = (e) => {
    e.preventDefault()
    socket.emit('send-message',{message});
    setChat((prev) => [...prev,{message:message, recieved:false}])
    setMessage("")
  };
  return (
    <Box sx = {{display: "flex", justifyContent: "center",}}>
        <Card sx={{padding:2, marginTop: 10, width: "60%", backgroundColor: "MintCream"}}>

        <Box sx={{marginBottom: 5}}></Box>
        {
          chat.map ((data) => (
            <Typography sx={{textAlign:data.recieved === true?"left":"right", paddingBottom: 1}} key={data.message}>{data.message}</Typography>
          ))
        }
        <Box component={"form"} onSubmit={(e) => handleForm(e)}>
        {
            typing===true
            ?
            <InputLabel shrink htmlFor="message-input">
                typing...
            </InputLabel>
            :<></>
        }
        <OutlinedInput
          sx={{backgroundColor: "white", width: "100%"}}
          id="message-input"
          value={message}
          placeholder='Write your message'
          onChange={(e) => handleInput(e)}
          endAdornment={
            <InputAdornment position="start" >
              <IconButton type='submit'>
                <SendIcon />
              </IconButton>
            </InputAdornment>
          }
          label="Amount"
        />
        </Box>
        </Card>
    </Box>
  ) 
}   

export default ChatWindow
