import { Box, Button, Card } from '@mui/material'
import React, { useEffect, useState } from 'react'
import { Link, useNavigate, useOutletContext } from 'react-router-dom'
import { v4 as uuidv4 } from 'uuid';

//background-image: linear-gradient(to right bottom, #051937, #004d7a, #008793, #00bf72, #a8eb12);
const Header = () => {
  const {socket} = useOutletContext();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([])

  const createNewRoom = (e) => {
    const roomId = uuidv4();
    navigate(`/room/${roomId}`);
    socket.emit('new-room-created',{roomId})
  }

  useEffect(() => {
    if(!socket) return
    socket.on('new-room-created', ({roomId}))
    setRooms([...rooms, roomId])
  }, [socket])
  

  return (
    <Card sx={{marginTop: 4, background: "gray"}}>
    <Box sx={{display:"flex", justifyContent: "space-between"}}>
        <Box>
          <Link style={{textDecoration:"none"}} to={"/"}>
              <Button sx={{color:"white"}} variant="text">Home</Button>
          </Link>
        </Box>
        {/* <Link style={{textDecoration:"none"}} to={`/room/${roomId}`}>
            <Button sx={{color:"white"}} variant="text">Rooms</Button>
        </Link> onClick={createNewRoom} */}
        <Box>
          <Button sx={{color:"white"}} variant="text" onClick={(e) => createNewRoom(e)}>New Room</Button>
        </Box>

    </Box>
    </Card>
  )
}

export default Header
