import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from 'react';
import { Box, Button, Card, IconButton, InputAdornment, InputLabel, OutlinedInput, Typography } from '@mui/material';
import { useOutletContext, useParams } from 'react-router-dom';


const ChatWindow = () => {
    const {socket} = useOutletContext();
    const [message, setMessage] = useState("");
    const [chat, setChat] = useState([]);
    const [typing, setTyping] = useState(false);
    const [typingTimeout,settypingTimeout] = useState(null)
    const {roomId} = useParams()


  useEffect(() => {
    if(!socket){return}
    socket.on('message-from-server',(data) => {
       setChat((prev) => [...prev,{message:data.message, recieved:true}])
    })
    socket.on('typing-started-from-server',() => {
        setTyping(true);
     })
     socket.on('typing-stoped-from-server',() => {
        setTyping(false);
     })
  }, [socket]);

  const handleInput = (e) => {
    setMessage(e.target.value);
    socket.emit('typing-started',{roomId});
    if(typingTimeout) clearTimeout(typingTimeout);
    settypingTimeout(setTimeout(() => {
        socket.emit("typing-stoped",{roomId})
    },1000))
  }

  const removeRoom = async() => {
    await fetch(`http://localhost:4000/rooms/${roomId}`,{
      method: "DELETE",
    })
    socket.emit('room-removed',{roomId})
  }
  const handleForm = (e) => {
    e.preventDefault()
    socket.emit('send-message',{message,roomId});
    setChat((prev) => [...prev,{message:message, recieved:false}])
    setMessage("")
  };
  return (
    
        <Card sx={{padding:2, marginTop: 10, width: "60%", backgroundColor: "MintCream"}}>
        <Box sx={{display:"flex", justifyContent: "space-between"}}>

        {
          roomId && <Typography>Room: {roomId}</Typography>
        }
        {
          roomId && <Button size = "small" variant='text' onClick={removeRoom} >Delete Room</Button>
        }
        </Box>
        <Box sx={{marginBottom: 5}}></Box>
        {
          chat.map ((data) => (
            <Typography sx={{textAlign:data.recieved === true?"left":"right", paddingBottom: 1}} key={data.message}>{data.message}</Typography>
          ))
        }
        <Box component={"form"} onSubmit={(e) => handleForm(e)}>
        {
            typing===true
            ?
            <InputLabel shrink htmlFor="message-input">
                typing...
            </InputLabel>
            :<></>
        }
        <OutlinedInput
          sx={{backgroundColor: "white", width: "100%"}}
          id="message-input"
          value={message}
          placeholder='Write your message'
          onChange={(e) => handleInput(e)}
          endAdornment={
            <InputAdornment position="start" >
              <IconButton type='submit'>
                <SendIcon />
              </IconButton>
            </InputAdornment>
          }
          label="Amount"
        />
        </Box>
        </Card>
  ) 
}   

export default ChatWindow
