import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import './App.css';
import { useEffect, useState } from 'react';
import {io} from "socket.io-client";
import { Box, Container, Typography } from '@mui/material';

function App() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    setSocket(io('http://localhost:4000'));
  }, []);

  useEffect(() => {
    if(!socket){return}
    socket.on('message-from-server',(data) => {
       setChat([...chat,data.message])
    })
  }, [socket]);

  const handleForm = (e) => {
    e.preventDefault()
    socket.emit('send-message',{message});
    setMessage("")
  };

  return (
    <div>
    <Box sx={{marginBottom: 5}}></Box>
    <Container>
    {
      chat.map ((message) => (
        <Typography key={message}>{message}</Typography>
      ))
    }
      <Box component={"form"} onSubmit={(e) => handleForm(e)}>
      <TextField size='small' id="standard-basic" label="Write your message" variant="standard" 
        value={message} onChange={(e) => setMessage(e.target.value)}
      />
      <Button variant="text" type='submit'>Send</Button>
      </Box>
    </Container>
    </div>

  );
}

export default App;
