import { Box, Button, Card } from '@mui/material'
import React, { useEffect, useState } from 'react'
import { Link, useNavigate} from 'react-router-dom'
import { v4 as uuidv4 } from 'uuid';
import Cookies from 'js-cookies'

//background-image: linear-gradient(to right bottom, #051937, #004d7a, #008793, #00bf72, #a8eb12);
export default function Header({socket, userId,setUserId}) {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([])

  const createNewRoom = () => {
    const roomId = uuidv4();
    navigate(`/room/${roomId}`);
    socket.emit('new-room-created',{roomId})
    setRooms([...rooms, roomId])
  }

  const Login = () => {
    const tempUid1 = (Cookies.hasItem('userId')?Cookies.getItem('userId'):null);
    console.log(tempUid1);
    const userId = uuidv4();
    Cookies.setItem('userId',userId);
    const tempUid2 = (Cookies.hasItem('userId')?Cookies.getItem('userId'):null);
    console.log();
    navigate("/");
  }
  const Logout = () => {
    setUserId(null);
    Cookies.removeItem('userId');
    navigate("/");
  }

  useEffect(() => {
    async function fetchRooms() {
      const res = await fetch('http://localhost:4000/rooms');
      const {rooms} = await res.json();
      setRooms(rooms);
    }
    fetchRooms()
  },[])

  useEffect(() => {
    if(!socket) return
    socket.on('new-room-created', ({roomId}) => {
      setRooms([...rooms, roomId])
    })
  }, [socket])
  

  return (
    <Card sx={{marginTop: 4, background: "gray"}}>
    <Box sx={{display:"flex", justifyContent: "space-between"}}>
        <Box>
          <Link style={{textDecoration:"none"}} to={"/"}>
              <Button sx={{color:"white"}} variant="text">Home</Button>
          </Link>
        
        {
          rooms.map((room) => (
            <Link key={room._id} style={{textDecoration:"none"}} to={`/room/${room.roomid}`}>
              <Button sx={{color:"white"}} variant="text">
                {room.name}
              </Button>
            </Link>
          ))

        }
        </Box>
        <Box>
          {
            userId &&
            <Box>
              <Button sx={{color:"white"}} variant="text" onClick={() => Logout()}>Logout</Button>
            </Box>
          }
          {
            !userId && 
            <Box>
              <Button sx={{color:"white"}} variant="text" onClick={() => Login()}>Login</Button>
            </Box>
          }
          <Box>
            <Button sx={{color:"white"}} variant="text" onClick={() => createNewRoom()}>New Room</Button>
          </Box>

        </Box>

    </Box>
    </Card>
  )
}
